BIGNUM * test_ClientProof()  {
    Srp *message = new Srp();
    BIGNUM *K = message->ClientProof(
        (char*)&"DEMO",
        (char*)&"demo",
        (char*)&"D863D2566A2381E73AC7DE76946A5CF3AB9C33208AE8A58B5B96481AFC457F8E",
        (char*)&"",
        (char*)&"",
        (char*)&""
    );
    delete message;

    return K;
}

void profile_client_proof(){
    BIGNUM * a = test_ClientProof();
    BN_free(a);
}

